    <script language="javascript" type="text/javascript">

        var gstrjobinfo = "%%";
        //global var to store jobsite info - gtg
        var gjobsiteinfo = "";
        var sr_ready = "no";
        var sr_pride = "%5expo1wbzazum%3d";
        var sr_stid = "5014";
        var sr_clid = "25350";
        var sr_locale = "3084";
        var sr_fn = "";
        var sr_ln = "";
        var enablesocialreferral = "no";
