        //        // for ldp256 accessibility - hide header, footer, progress bars if empty
        $(document).ready(function() {
            var hideprog = "1";
            var hidehead = "1";
            var hidefoot = "1";

            if (hideprog == 1 && $("#ctl00_progressbar") != null) {
                $("#ctl00_progressbar").hide();
            }
            if (hidehead == 1 && $("#ctl00_header") != null) {
                $("#ctl00_header").hide();
            }
            if (hidefoot == 1 && $("#ctl00_footer") != null) {
                $("#ctl00_footer").hide();
            }

            if ($("span[id='ctl00_maincontent_spnloginenumeration']") != null && $("span[id='ctl00_maincontent_spnloginenumeration']").length > 0 &&
                $("span[id='ctl00_maincontent_spnloginenumeration']")[0].innerhtml.length > 0) {
                $("input[id='email']").focus();
            }
        });              

        function setreadyforsocialreferral(ready, clid, stid, pride, localeid, element, fn, ln) {
            sr_ready = ready;
            sr_pride = pride;
            sr_stid = stid;
            sr_clid = clid;
            sr_locale = localeid;
            sr_fn = fn;
            sr_ln = ln;
            if (sr_ready == "yes") {
                launchsocialreferralmenu(document.getelementbyid(element));
            }
        }
        function launchsocialreferralmenu(element) {

            var urlstring = "../tgnewui/referral/home/referralworkflowoptions?siteid=" + sr_stid + "&partnerid=" + sr_clid + "&localeid=" + sr_locale + "&bruid=" + sr_pride + "&jobids=" + "&fn=" + sr_fn + "&ln=" + sr_ln
            window.dialogbuilder.showdialog(element, urlstring);
        }
        function socialnetworkreferral(element) {
            if (sr_ready == "yes")
                launchsocialreferralmenu(element);
            else {

                document.frmsocialnetworkreferral.buttonid.value = element.id;
                openwindowbysize2('about:blank?sessionid=7ba5c87e08d5eb4036a6bb7dbdde8e09367d', 900, 720);
                document.frmsocialnetworkreferral.submit();
            }
        }

        function setcmdselecttext() { // for setting the cmdselect text
            var lchkcount;
            var chkboxes;
            var cmdselect;
            var ballselected = true;
            var lchkidx;

            if (typeof (document.frmresults.chkjobclientids.length) != 'undefined') {
                chkboxes = document.frmresults.chkjobclientids;
                lchkcount = chkboxes.length;
                for (lchkidx = 0; lchkidx < lchkcount; lchkidx++) {
                    if (!chkboxes[lchkidx].checked) {
                        ballselected = false;
                        break;
                    }
                }
            }
            else {
                if (!document.frmresults.chkjobclientids.checked) {
                    ballselected = false;
                }
            }

            cmdselect = document.frmresults.cmdselect;
            // need to keep grid columnheader label in sync with checkbox state
            var selectalllbl = cmdselect.id;
            selectalllbl += " " + document.getelementbyid("hcheckbox").id;
            if (ballselected) {
                cmdselect.checked = true;
                cmdselect.setattribute("aria-checked", "true");
                document.frmresults.checkit.value = "false";
                selectalllbl += " " + document.getelementbyid("hchecked").id;
            }

            if (!ballselected) {
                cmdselect.checked = false;
                cmdselect.removeattribute("checked");
                cmdselect.setattribute("aria-checked", "false");
                document.frmresults.checkit.value = "true";
                selectalllbl += " " + document.getelementbyid("hunchecked").id;
            }
            // now find the parent th to set the aria label
            var parent = document.frmresults.cmdselect.parentnode;
            while (parent != null && parent != undefined && parent.tagname != "th") {
                parent = parent.parentnode;
            }
            if (parent.tagname == "th") {
                parent.setattribute("aria-labelledby", selectalllbl);
            }

        }

        function massviewjobs() {
            if (gstrjobinfo.length == 2) {
                alert(msg_cim_home_seljob);
                return false;
                }
            else {
                document.frmresults.jobinfo.value = gstrjobinfo;
                document.frmresults.jobsiteinfo.value = gjobsiteinfo;
                document.frmresults.submit();
            }
        }

        //09-17-02 - gtg changes - strjobreqlang newly added
        function oncheckjob(lngcontrolid, lngjobid, strjobreqlang, strgqid, balljobs) {
            var strtemp;
            var lngpos;

            //gtg local vars for jobsite info
            var strtemp1;
            var lngpos1;
            var strjobsiteid;
            var bjobchk;
            var selectlbl;
            var parent;

            //check if hidjobsiteid value is an array
            if (typeof (document.frmresults.hidjobsiteid.length) == 'undefined') {
                strjobsiteid = document.frmresults.hidjobsiteid.value;
                bjobchk = document.frmresults.chkjobclientids.checked;
                document.frmresults.chkjobclientids.setattribute("aria-checked", bjobchk);
                selectlbl = document.frmresults.chkjobclientids.id;
                parent = document.frmresults.chkjobclientids.parentnode;
            }
            else {
                strjobsiteid = document.frmresults.hidjobsiteid[lngcontrolid].value;
                bjobchk = document.frmresults.chkjobclientids[lngcontrolid].checked;
                document.frmresults.chkjobclientids[lngcontrolid].setattribute("aria-checked", bjobchk);
                selectlbl = document.frmresults.chkjobclientids[lngcontrolid].id;
                parent = document.frmresults.chkjobclientids[lngcontrolid].parentnode;
            }

            selectlbl += " " + document.getelementbyid("hcheckbox").id;
            // need to keep grid rowheader label in sync with checkbox state
            if (bjobchk == true) {
                selectlbl += " " + document.getelementbyid("hchecked").id;
            }
            else {
                selectlbl += " " + document.getelementbyid("hunchecked").id;
            }
            // now find the parent td to set the aria label
            while (parent != null && parent != undefined && parent.tagname != "td") {
                parent = parent.parentnode;
            }
            if (parent.tagname == "td") {
                parent.setattribute("aria-labelledby", selectlbl);
            }


            if (bjobchk) {// add to jobinfo
                if (gstrjobinfo.indexof("%%" + lngjobid + "|" + strjobreqlang + "|" + strgqid + "%%") == -1) {
                    gstrjobinfo = gstrjobinfo + lngjobid + "|" + strjobreqlang + "|" + strgqid + "%%";
                    //gtg - add jobsiteid
                    gjobsiteinfo = gjobsiteinfo + strjobsiteid + ",";

                }
            }
            else {// remove from jobinfo
                lngpos = gstrjobinfo.indexof("%%" + lngjobid + "|" + strjobreqlang + "|" + strgqid + "%%") + 2; // to exclude the %% (prefix)

                if (lngpos > 1) {
                    strtemp = lngjobid + "|" + strjobreqlang + "|" + strgqid + "%%";
                    gstrjobinfo = gstrjobinfo.substr(0, lngpos) + gstrjobinfo.substr(lngpos + strtemp.length, gstrjobinfo.length - lngpos - strtemp.length);
                }

                lngpos1 = gjobsiteinfo.indexof(strjobsiteid + ",");

                strtemp1 = strjobsiteid + ",";
                gjobsiteinfo = gjobsiteinfo.substr(0, lngpos1) + gjobsiteinfo.substr(lngpos1 + strtemp1.length, gjobsiteinfo.length - lngpos1 - strtemp1.length);

            }

            if (!balljobs) {
                setcmdselecttext(); // for setting the cmdselect text
            }

        }

        function checkall() {
            // this will modify the jobinfo value as necessary
            var arrinfo;
            var lchkidx;
            var lchkcount;
            var chkboxes;
            var checkit = document.frmresults.checkit;
            var bchecked = checkit.value == "true" ? true : false;

            if (typeof (document.frmresults.chkjobclientids.length) != 'undefined') {
                chkboxes = document.frmresults.chkjobclientids;
                lchkcount = chkboxes.length;
                for (lchkidx = 0; lchkidx < lchkcount; lchkidx++) {
                    chkboxes[lchkidx].checked = bchecked;
                    chkboxes[lchkidx].setattribute("aria-checked", checkit.value);
                    if (checkit.value == "false") {
                        chkboxes[lchkidx].removeattribute("checked");
                    }
                    arrinfo = chkboxes[lchkidx].value.split("|");
                    oncheckjob(lchkidx, arrinfo[0], arrinfo[1], arrinfo[2], true);
                }
            }
            else {
                document.frmresults.chkboxes.checked = bchecked;
                document.frmresults.chkboxes.setattribute("aria-checked", checkit.value);
                if (checkit.value == "false") {
                    document.frmresults.chkboxes.removeattribute("checked");
                }
                arrinfo = document.frmresults.chkjobclientids.value.split("|");
                oncheckjob(0, arrinfo[0], arrinfo[1], arrinfo[2], true)
            }
            // build aria-label to keep the table cell label in sync with checkbox
            var selectalllbl = document.frmresults.cmdselect.id;
            selectalllbl += " " + document.getelementbyid("hcheckbox").id;
            if (checkit.value == "true") {
                selectalllbl += " " + document.getelementbyid("hchecked").id;
            }
            else {
                selectalllbl += " " + document.getelementbyid("hunchecked").id;
            }
            // now find the parent tg to set the aria label
            var parent = document.frmresults.cmdselect.parentnode;
            while (parent != null && parent != undefined && parent.tagname != "th") {
                parent = parent.parentnode;
            }
            if (parent.tagname == "th") {
                parent.setattribute("aria-labelledby", selectalllbl);
            }


            document.frmresults.checkit.value = checkit.value == "true" ? "false" : "true";

        }

        function deselectall() {
            var chkjobclientids;
            var lchkidx;
            var lchkcount;
            var arrinfo;

            if (typeof (document.frmresults.chkjobclientids.length) != 'undefined') {
                chkjobclientids = document.frmresults.chkjobclientids;
                lchkcount = chkjobclientids.length; 
                for (lchkidx = 0; lchkidx < lchkcount; lchkidx++) {
                    chkjobclientids[lchkidx].checked = false;
                    chkjobclientids[lchkidx].setattribute("aria-checked", "false");
                    chkjobclientids[lchkidx].removeattribute("checked");
                    arrinfo = chkjobclientids[lchkidx].value.split("|");
                    oncheckjob(lchkidx, arrinfo[0], arrinfo[1], arrinfo[2], true);
                }
